import axios from 'axios'
const baseUrl = 'http://localhost:3001/persons'

const getAll = () => {
    const request = axios.get(baseUrl)
    return request.then(response => response.data)
}

const create = newPerson => {
    const request = axios.post(baseUrl, newPerson)
    return request.then(response => response.data)
}

const remove = id => {
    const personURL = `${baseUrl}/${id.value}`
    const request = axios.delete(personURL)
    return request.then(response => response.data)
}

const swap = (newPerson, people) => {
    const replacedPerson = people.find(p => p.name === newPerson.name)
    const updatedNumber = {...replacedPerson, number : newPerson.number}
    axios.put(baseUrl, updatedNumber).then(response => response.data)
}



export default { getAll, create, remove, swap }
