import axios from 'axios'
const baseUrl = 'http://localhost:3001/persons'

const getAll = () => {
    const request = axios.get(baseUrl)
    return request.then(response => response.data)
}

const create = newPerson => {
    const request = axios.post(baseUrl, newPerson)
    return request.then(response => response.data)
}

const remove = id => {
    const confirm = window.confirm(`Are you sure you want to delete ${id.name} ?`)
    const personURL = `${baseUrl}/${id.value}`
    const request = axios.delete(personURL).then(response => console.log(response.data))
    confirm ? request() : console.log("nope.")
}



export default { getAll, create, remove }
